import React from "react"; 

function Search({ handleInput }) {
  return (
    <section className="search">
      <input 
        className="search-box" 
        placeholder="Search for a movie" 
        type="text"
        autoFocus={true} 
        onChange={handleInput}
      />
      <i className="fas fa-search fa-xs"></i>
    </section>
  )
}

export default Search;
